import React, { useState } from "react";
import Header from "./components/Header"
import Footer from "./components/Footer"
import Note from  "./components/Note"
import InputNote from "./components/InputNote"
import notes from "./notes"


function App() {

    const [notesState, update] = useState(notes);

    function createNotes(note){
        return <Note key={note.key} title= {note.title} content= {note.content} deleteNote={deleteNote}/>
    }
    
    function deleteNote(key){
        update(prevNotes =>{
            return prevNotes.filter((currentNote,index =>{
                return key !== index;
            }))
        })
    }

    function addNote(title,content){
        var keyVal = notesState.length+1;
        var newNote = {
            key: [keyVal],
            title: [title],
            content: [content]
        };

        update(prevNotes => {
            return [newNote,...prevNotes];
        });
    }

    return (
    <div>
        <Header />
        <InputNote add={addNote} />
        <div className = "grid">
        {notesState.map(createNotes)}
        </div>
        <Footer />
    </div>
    );
}

export default App;
