import React, { useState } from "react";

function InputNote(props){

    const [input, setInput] = useState({
        title: "",
        content: ""
    });

    function add(event){
        event.preventDefault();
        props.add(input.title,input.content);
    }

    function handleChange(event){
        const {source, value} = event.target;

        setInput(prevValue =>{
            return {
                ...prevValue,
                [source]: value
            };
        });
    }

    return (
        <div>
            <form>
                <input placeholder="Tilte" value={input.title}/>
                <textarea placeholder="Content" onChange={handleChange} value={input.content} />
                <button onClick= {add}>ADD</button>
            </form>
        </div>
    );
}

export default InputNote
